$(document).ready(function(){$(".button.select").on("click",function(e){e.preventDefault(),$(this).next(".dropdown-menu").slideToggle("fast")}),$("ul.dropdown-menu li").on("click",function(){var e=$(this).data("variant");variantLabel=e.toLowerCase().replace(/\b[a-z]/g,function(e){return e.toUpperCase()});var a;switch(e){case"lemon":a="src/product-images/Lemon.png";break;case"sandalwood":a="src/product-images/Sandalwood.png";break;case"lavender":a="src/product-images/Lavender.png"}$("#test-variable-image").attr("src",a),$(".variation").text(e),$(this).parent().prev("button").text(variantLabel),$(this).parent().slideToggle("fast")}),$(".buy-now, #buynow-alert, img").on("click",function(){$("#buynow-alert").fadeToggle("medium")})});